import React from 'react';
import useFetch from './useFetch';
import { Button, Col, Row } from 'react-bootstrap';
import BookList from './BookList';
import ProductList from './ProductList';
import SalesList from "./SalesList";

function MyComponent() {
    const urls = ['http://localhost:8000/customer', 'http://localhost:8000/product', 'http://localhost:8000/sales'];
    const { data, error, isPending } = useFetch(urls);

    return (
        <div>
            {isPending && <div>Loading...</div>}
            {error && <div>Error: {error}</div>}

            {data && (
                <div>
                    <Row>
                        <Col>
                            <h2>Customers</h2>
                            <BookList customers={data[0]} title="Customer List" />
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <h2>Products</h2>
                            <ProductList products={data[1]} title="Product List" />
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <h2>Sales</h2>
                            {/* Assuming Sales component is similar to BookList and ProductList */}
                            <SalesList sales={data[2]} title="Sales List" />
                        </Col>
                    </Row>
                </div>
            )}
        </div>
    );
}

export default MyComponent;
