import React, {useState} from 'react';
import {useNavigate, useParams} from 'react-router-dom';
import useFetch from './useFetch';

function CustomerDetails(props) {
    const { id } = useParams();
    const urls = ['http://localhost:8000/customer/' + id];
    const { data, error, isPending } = useFetch(urls);
    const customer = data[0];
    console.log('Customer D:', customer); // Log the customer data
    const [name, setName] = useState(customer.name);
    const [email, setEmail] = useState(customer.email);

    const navigate = useNavigate();


    const handleSubmit = (e) => {
        e.preventDefault();
        const customer = {name, email };
        console.log(customer);

        fetch(`http://localhost:8000/customer/${id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(customer),
        })
            .then(() => {
                console.log('New customer added');
                // Redirect or perform any other action after successful submission
                navigate('/customers');
            })
    };

    return (
        <div className="create">
            <h2> Add a new Customer</h2>
            <form onSubmit={handleSubmit}>
                <label>Customer name: </label>
                <input
                    type="text"
                    required
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <div>
                    <label>Customer Email: </label>
                    <input
                        type="email"
                        required
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div>
                    <button> Insert Customer</button>
                </div>
                {error && <p className="error-message">{error}</p>}
                <p>{name}</p>
                <p>{email}</p>
            </form>
        </div>
    );
}

export default CustomerDetails;
