import React, {useState} from 'react';
import "./CreateStyle.css"
import {useNavigate} from "react-router-dom";

function InsertCustomer(props) {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const history = useHistory();

    const handleSubmit = async () => {
        try {
            // Make a POST request to your API's add customer endpoint
            const response = await fetch('http://localhost:8000/customer', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ CustomerName: name, CustomerEmail: email }),
            });

            if (response.ok) {
                // Customer added successfully, navigate to the customer list page or any other page
                history.push('/customers');
            } else {
                // Handle the error appropriately
                console.error('Error adding customer:', response.statusText);
            }
        } catch (error) {
            console.error('Error adding customer:', error);
        }
    };
    return (
        <div className="create">
            <h2> Add a new Customer</h2>
            <form onSubmit={handleSubmit}>
                <label>Customer name: </label>
                <input type="text"
                       required
                       value={name}
                       onChange ={(e) => setName(e.target.value)}>

                </input>
                <div>
                <label>Customer Email: </label>
                <input type="text"
                       required
                       value={email}
                       onChange ={(e) => setEmail(e.target.value)}
                ></input>
                </div>
                <div>
                <button > Insert Customer </button>
                </div>
                <p>{name}</p>
                <p>{email}</p>
            </form>
        </div>
    );
}

export default InsertCustomer;
