import {Col, Row, Button} from "react-bootstrap";
import {useState, useEffect} from "react";
import customerList from "./BookList";
import useFetch from "./useFetch";

function Books () {
    let url = " http://localhost:8000/customer";
    const {data : customers, isPending, error} = useFetch( url )
    const myTitle = "Customers"
    const myTitle14 = " Books at 14.99"
    return (
        <Row>
            <Col sm={3}>
            </Col>
            <Col sm={3}>
                { error && <div> Error: {error} </div> }
                {isPending && <div> Loading ...</div>}
                { customers && <customerList customers={customers} title={myTitle}/>}
            </Col></Row>
    )
}
export default Books;
