import {Col, Row, Button} from "react-bootstrap";
import React, {useState, useEffect} from "react";
import BookList from "./BookList";
import useFetch from "./useFetch";
import { useNavigate } from 'react-router-dom';
import SalesList from "./SalesList";


function Books () {
    const urls = ['http://localhost:8000/customer', 'http://localhost:8000/product', 'http://localhost:8000/month'];
    const { data, error, isPending } = useFetch(urls);
    const navigate = useNavigate();
    const myTitle = "Customers"
    const myTitle14 = " Books at 14.99"
    return (
        <Row>
            <Col sm={3}>
            </Col>
            <Col sm={3}>
                { error && <div> Error: {error} </div> }
                {isPending && <div> Loading ...</div>}
                { data[0] && <BookList data={data[0]} title={myTitle}/>}
            <Button onClick={() => navigate('/InsertCustomer')}>Add New Customer</Button>
            </Col>
        </Row>
    )
}
export default Books;
