            method: 'DELETE',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(deletedCustomer),
        })
            .then(() => {
            })
    };

    return (
        <form onSubmit={handleSubmit}>
            <button> Delete Customer</button>
        </form>
            );

}
