import React from 'react';
import useFetch from './useFetch';
import { Col, Row } from 'react-bootstrap';

function MyComponent() {
    const urls = ['http://localhost:8000/customer', 'http://localhost:8000/product', 'http://localhost:8000/month'];
    const { data, error, isPending } = useFetch(urls);

    // Render based on the fetched data
    return (
        <div>
            {isPending && <div>Loading...</div>}
            {error && <div>Error: {error}</div>}

            {data && (
                <div>
                    <Row>
                        <Col>
                            <h2>Customers</h2>
                            <ol>
                                {data[0]?.map((customer) => (
                                    <li key={customer.id}>{customer.name}</li>
                                ))}
                            </ol>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <h2>Products</h2>
                            <ol>
                                {data[1]?.map((product) => (
                                    <li key={product.id}>-{product.ItemName}: {product.TotalSales}</li>
                                ))}
                            </ol>
                        </Col>
                    </Row>
                    <Row>
                        <Col>
                            <h2>Monthly Sales</h2>
                            <ol>
                                {data[2]?.map((sale) => (
                                    <li key={sale.Date}>{`${sale.Date}: ${sale.TotalSales}`}</li>
                                ))}
                            </ol>
                        </Col>
                    </Row>
                </div>
            )}
        </div>
    );
}

export default MyComponent;
